import React from 'react';



class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userDice1: 0,
            userDice2: 0,
            enemyDice1: 0,
            enemyDice2: 0,
            eSpirit: 100,
            uSpirit: 100,


        };
    }





    rollDice = () => {
        const userD1Value = Math.floor(Math.random() * 6 + 1);
        const userD2Value = Math.floor(Math.random() * 6 + 1);
        const enemyD1Value = Math.floor(Math.random() * 6 + 1);
        const enemyD2Value = Math.floor(Math.random() * 6 + 1);

        this.setState({
            userDice1: userD1Value,
            userDice2: userD2Value,
            enemyDice1: enemyD1Value,
            enemyDice2: enemyD2Value,

        })

    } 

    winSubract = () => {
        let userValuex = this.state.userDice1 + this.state.userDice2;
        let enemyValuex = this.state.enemyDice1 + this.state.enemyDice2;
        const winDif = userValuex - enemyValuex;

        let newEspirit = this.state.eSpirit - winDif;

        this.setState({
            eSpirit: newEspirit
        })






    }







    render() {


        let userValue = this.state.userDice1 + this.state.userDice2;
        let enemyValue = this.state.enemyDice1 + this.state.enemyDice2;
        let result = 'start game'
        let winDif = userValue - enemyValue;
        let lossDif = enemyValue - userValue;


        if (userValue > enemyValue) {
            result = "winner"
        } else if (userValue === enemyValue) {
            result = 'draw'
        } else {
            result = "looser"
        };

        if (result === 'winner') {
            this.winSubract()
        } 







        return (
            <div className="game-screen">
                <div className='container'>
                    <div className='dice-container'>

                    </div>
                    <h1> user : {userValue}</h1>
                    <h1> CPU : {enemyValue}</h1>
                    <h3>{result} </h3>
                    <h2>user Spirit: {this.state.uSpirit}</h2>
                    <h2>enemy Spirit: {eSpirit} </h2>
                    <button onClick={this.rollDice} >Roll</button>

                </div>
            </div>
        );
    }
}


export default Game;
