import React, { useState } from 'react'
import FormInput from '../Components/Form-Components/FormInput'
import CustomButton from '../Components/Form-Components/CustomButton'
import { Link } from 'react-router-dom';


import Login from '../Components/OtherComponents/Login';
import SignUp from '../Components/OtherComponents/SignUp';
import './SplashScreenStyles.scss'
import Game from './Game';



const SplashScreen = () => {

    const [PlayGame, setPlayGame] = useState(false);

    const switchToGameScreen = ( ) => {
        setPlayGame(!PlayGame);
    }

    if (!PlayGame) {

        return (
            <div className='Splash-Page'>
                <Login className='login-section'  switchToGameScreen={switchToGameScreen} />
                <SignUp className='sign-up-section' switchToGameScreen={switchToGameScreen} />
            </div>
        );

    }else {
        return (
            <Game/>
        )

    }



}

export default SplashScreen;
