import React from 'react';

import './App.scss';
import './App.css';
import MainMenu from './Screen/MainMenu';
import Login from './Components/OtherComponents/Login'
import SignUp from './Components/OtherComponents/SignUp'


class App extends React.Component {
  constructor(){
    super()
    this.state = {
      signedIn: false,
      profile: {
        id: '',
        userName: '',
        email: '',
        score: '',
      }
  };

  }



  componentDidMount(){
    fetch('http://localhost:3000/')
    .then(response => response.json())
    .then(console.log)
  }


  loaduser = (data) => {
    this.setState({
      profile: {
        id: data.id,
        userName: data.userName,
        email: data.email,
        score: data.score
      }
    })
  }

  switchToGameScreen = () => {
    this.setState((prevState) => {
        return { signedIn: !prevState.signedIn };
    });
}

  render () {
    if (!this.state.signedIn) {

      return (
          <div className='Splash-Page'>
              <Login className='login-section'  STGS={this.switchToGameScreen} loadUser={this.loaduser}/>
              <SignUp className='sign-up-section' STGS={this.switchToGameScreen} loadUser={this.loaduser}/>
          </div>
      );

  }else {
      return (
          <MainMenu Profile={this.state.profile}/>
      );

  }
}
}

export default App;
