import React, { useState } from 'react';
import { Link } from 'react-router-dom'
import Rules from '../Components/ModalComponents/Rules'
import Credits from '../Components/ModalComponents/Credits'




const MainMenu = ({
    profile
}) => {

    const [showRules, setShowRules] = useState(false);

    const [showCredits, setShowCredits] = useState(false);

    const toggleRules = () => {
        setShowRules(!showRules)
    }

    const toggleCredits = () => {
        setShowCredits(!showCredits)
    }


    return (<div>

        {showCredits &&
            <Credits toggleCredits={toggleCredits} />}

        {showRules && <Rules toggleRules={toggleRules} />}


        <h1>{profile.userName}</h1>




        <button onClick={toggleRules}>How to Play</button>

        <Link to='/Game'>
            <button>Play</button>
        </Link>

        <button onClick={toggleCredits}>Credits</button>


    </div>



    )

}

export default MainMenu;
