import React from 'react';

import './App.css';
import MainMenu from './Screen/MainMenu';
import Login from './Components/OtherComponents/Login'
import SignUp from './Components/OtherComponents/SignUp'

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      playGame: false,
      profile: {
        id: '',
        userName: '',
        email: '',
        score: '',
      }
  };

  }



  componentDidMount(){
    fetch('http://localhost:3000/')
    .then(response => response.json())
    .then(console.log)
  }


  loaduser = (data) => {
    this.setState({
      profile: {
        id: data.id,
        userName: data.userName,
        email: data.email,
        score: data.score
      }
    })
  }

  render () {
    if (!PlayGame) {

      return (
          <div className='Splash-Page'>
              <Login className='login-section'  STGS={switchToGameScreen} loadUser={loadUser}/>
              <SignUp className='sign-up-section' STGS={switchToGameScreen} loadUser={loadUser}/>
          </div>
      );

  }else {
      return (
          <MainMenu Profile={this.state.profile}/>
      );

  }
}
}

export default App;
