import React from 'react';



class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userDice1: 0,
            userDice2: 0,
            enemyDice1: 0,
            enemyDice2: 0,
            result: " ",
            eSpirit: 30,
            uSpirit: 30,


        };
    }





    rollDice = () => {
        const userD1Value = Math.floor(Math.random() * 6 + 1);
        const userD2Value = Math.floor(Math.random() * 6 + 1);
        const enemyD1Value = Math.floor(Math.random() * 6 + 1);
        const enemyD2Value = Math.floor(Math.random() * 6 + 1);
        const userValue = userD1Value + userD2Value;
        const enemyValue = enemyD1Value + enemyD2Value;

        const winSubract = () => {
            const winDif = userValue - enemyValue;
            let newEspirit = this.state.eSpirit - winDif;
            this.setState({
                eSpirit: newEspirit
            })
        }

        const looseSubract = () => {
            const lossDif = enemyValue - userValue;
            let newUspirit = this.state.uSpirit - lossDif;
            this.setState({
                uSpirit: newUspirit
            })
        }

        const gameOver = ()=>{
            alert('game over')
        }



        if (userValue > enemyValue) {
            this.setState({
                result: "winner"
            })
            winSubract()
        } else if (userValue === enemyValue) {
            this.setState({
                result: "draw"
            })
        } else {
            this.setState({
                result: "Looser"
            })
            looseSubract()
        };

        this.setState({
            userDice1: userD1Value,
            userDice2: userD2Value,
            enemyDice1: enemyD1Value,
            enemyDice2: enemyD2Value,

        })
    }











    render() {

        if(this.state.eSpirit <= 0) {
            alert('game over')
        }




        return (
            <div className="game-screen">
                <div className='container'>
                    <div className='dice-container'>

                    </div>
                    <h1> user : {this.state.userDice1 + this.state.userDice2}</h1>
                    <h1> CPU : {this.state.enemyDice1 + this.state.enemyDice2}</h1>
                    <h3>{this.state.result}</h3>
                    <h2>user Spirit: {this.state.uSpirit}</h2>
                    <h2>enemy Spirit: {this.state.eSpirit} </h2>
                    <button onClick={this.rollDice} >Roll</button>

                </div>
            </div>
        );
    }
}


export default Game;
