import React from 'react';
import HealthBar from '../Components/Game-Components/HealthBar'




class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userDice1: 0,
            userDice2: 0,
            enemyDice1: 0,
            enemyDice2: 0,
            result: " ",
            eSpirit: 20,
            uSpirit: 20,
            userWins: 0,
            enemyWins: 0


        };
    }

    resetGame = () => {
        this.setState({
            eSpirit: 20,
            uSpirit: 20,
            userWins: 0,
            enemyWins: 0

        })
    }

    resetSpirit = () => {
        this.setState({
            eSpirit: 20,
            uSpirit: 20

        })
    }


    addUserPoint = () => {
        this.setState({
            userWins: this.state.userWins + 1
        })
    }

    addEnemyPoint = () => {
        this.setState({
            enemyWins: this.state.enemyWins + 1
        })
    }

    rollDice = () => {
        let userD1Value = Math.floor(Math.random() * 6 + 1);
        let userD2Value = Math.floor(Math.random() * 6 + 1);
        const enemyD1Value = Math.floor(Math.random() * 6 + 1);
        const enemyD2Value = Math.floor(Math.random() * 6 + 1);

        const userValue = userD1Value + userD2Value;
        const enemyValue = enemyD1Value + enemyD2Value;
        const lossDif = enemyValue - userValue;
        const winDif = userValue - enemyValue;

        const winSubract = () => {
            this.setState({
                eSpirit: this.state.eSpirit - winDif
            })
        }

        const looseSubract = () => {
            this.setState({
                uSpirit: this.state.uSpirit - lossDif
            })
        }

        // Result Decision

        if (userD1Value === userD2Value && userValue > enemyValue) {
            this.setState({
                result: "dodge attack"
            })
        } else if (enemyD1Value === enemyD2Value && userValue < enemyValue) {
            this.setState({
                result: "missed"
            })
        } else if (enemyD1Value === enemyD2Value && userD1Value === userD2Value) {
            this.setState({
                result: 'block'
            })
        }

        if (userValue > enemyValue) {
            this.setState({
                result: "winner"
            })
            winSubract()
        } else if (userValue === enemyValue) {
            this.setState({
                result: "draw"
            })
        } else {
            this.setState({
                result: "Looser"
            })
            looseSubract()
        };

        this.setState({
            userDice1: userD1Value,
            userDice2: userD2Value,
            enemyDice1: enemyD1Value,
            enemyDice2: enemyD2Value,

        })
    }











    render() {

        if (this.state.eSpirit <= 0) {
            this.addUserPoint();
              if(this.state.userWins >= 2){
                  this.resetGame();
                  fetch('http://localhost:3000/score',{
                    method: 'put',
                    headers: {'Content-Type': 'application/json'},
                    body: JSON.stringify({
                        id: this.props.Profile.id,

                    })
                  }).then(response => response.json())
                  .then(score => {
                      this.props.updateScore(score);
                  })

              }else{
                  this.resetSpirit();
              }
        } else if (this.state.uSpirit <= 0) {
            this.addEnemyPoint();
            if(this.state.enemyWins >= 2) {
                alert('gameover You Loose')
                this.resetGame();  
            }else{
                this.resetSpirit();
            }
        }




        return (
            <div className="game-screen">
                <div className='container'>
                    <div className='dice-container'>

                    </div>
                    <h1> {this.props.Profile.userName} : {this.state.userDice1 + this.state.userDice2}</h1>
                    <h4> wins - {this.state.userWins}</h4>
                    <h3> {this.state.userDice1} : {this.state.userDice2}</h3>
                    <h3>Health: {this.state.uSpirit}</h3>
                    <HealthBar health={this.state.uSpirit}/>
                    <h1> CPU score: {this.state.enemyDice1 + this.state.enemyDice2}</h1>
                    <h4> wins - {this.state.enemyWins}</h4>
                    <h3> {this.state.enemyDice1} : {this.state.enemyDice2}</h3>
                    <h3>enemy Health: {this.state.eSpirit} </h3>


                    <h2>{this.state.result}</h2>


                    <button onClick={this.rollDice} >Roll</button>
                    <br></br>
                    <br></br>
                    <button onClick={this.resetGame}> Reset Game</button>
                    <br></br>
                    <br></br>
                   <button onClick={this.props.tg}> home </button>

                </div>
            </div>
        );
    }
}


export default Game;
