import React, { useState } from 'react'
import Login from '../Components/OtherComponents/Login';
import SignUp from '../Components/OtherComponents/SignUp';
import './SplashScreenStyles.scss'

import MainMenu from './MainMenu';



const SplashScreen = () => {

    const [PlayGame, setPlayGame] = useState(false);

    const switchToGameScreen = ( ) => {
        setPlayGame(!PlayGame);
    }

    if (!PlayGame) {

        return (
            <div className='Splash-Page'>
                <Login className='login-section'  STGS={switchToGameScreen} />
                <SignUp className='sign-up-section' STGS={switchToGameScreen} />
            </div>
        );

    }else {
        return (
            <MainMenu/>
        );

    }



}

export default SplashScreen;
