import React from 'react';
import FormInput from '../Form-Components/FormInput';
import {Redirect} from 'react-router-dom'




import './SignUpStyles.scss'

class SignUp extends React.Component {
    constructor() {
        super();

        this.state = {
            userName: '',
            email: '',
            password: '',
            confirmPassword: '',
            Guest: false

        };
    }


    handleGuest = () => {
        this.setState((prevState) => {
            return { Guest: !prevState.Guest };
        });
    }

    handleSubmit = async event => {


        event.preventDefault();
        if (!this.state.Guest) {
            return <Redirect to='/Game'/>

        } else {
            const { userName, email, password, confirmPassword } = this.state;

            if (password !== confirmPassword) {
                alert("Password don't match");
                return;
            }
        }





    };

    handleChange = event => {
        const { name, value } = event.target;

        this.setState({ [name]: value });
    };

    render() {
        const { userName, email, password, confirmPassword, Guest } = this.state;
        return (
            <div className='sign-up'>
                <h2 className='title'>I do not have an account</h2>
                <span>Sign up with an email and password</span>
                <form className='sign-up-form' onSubmit={this.handleSubmit}>
                    <input type='checkbox' name='guest' value={Guest} onClick={this.handleGuest} />
                    <span>Play as a guest</span>

                    <FormInput

                        type='text'
                        name='userName'
                        value={userName}
                        onChange={this.handleChange}
                        label='User Name' required
                    />


                    {!this.state.Guest ?

                        <div>
                            <FormInput

                                type='email'
                                name='email'
                                value={email}
                                onChange={this.handleChange}
                                label='Email' required
                            />
                            <FormInput

                                type='password'
                                name='password'
                                value={password}
                                onChange={this.handleChange}
                                label='Password' required
                            />
                            <FormInput

                                type='password'
                                name='confirmPassword'
                                value={confirmPassword}
                                onChange={this.handleChange}
                                label='Confirm Password' required
                            />
                            <button type='submit'>Sign Up</button>
                        </div> :

                        <div>
                            <button>play as guest</button>
                        </div>
                    }
                </form>
            </div>
        )
    }
}

export default SignUp;
