import React from 'react';



class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userDice1: 0,
            userDice2: 0,
            enemyDice1: 0,
            enemyDice2: 0,
            result: " ",
            eSpirit: 20,
            uSpirit: 20,
            userWins: 0,
            enemyWins: 0


        };
    }

    resetGame = () => {
        this.setState({
            eSpirit: 20,
            uSpirit: 20,
            userWins: 0,
            enemyWins: 0

        })
    }

    resetSpirit = () => {
        this.setState({
            eSpirit: 20,
            uSpirit: 20

        })
    }


    addUserPoint = () => {
        let point = this.state.userWins + 1;
        this.setState({
            userWins: point
        })
    }

    addEnemyPoint = () => {
        let point = this.state.enemyWins + 1;
        this.setState({
            enemyWins: point
        })
    }

    rollDice = () => {
        let userD1Value = Math.floor(Math.random() * 6 + 1);
        let userD2Value = Math.floor(Math.random() * 6 + 1);
        const enemyD1Value = Math.floor(Math.random() * 6 + 1);
        const enemyD2Value = Math.floor(Math.random() * 6 + 1);

        const userValue = userD1Value + userD2Value;
        const enemyValue = enemyD1Value + enemyD2Value;
        const lossDif = enemyValue - userValue;
        const winDif = userValue - enemyValue;

        const winSubract = () => {
            let newEspirit = this.state.eSpirit - winDif;
            this.setState({
                eSpirit: newEspirit
            })
        }

        const looseSubract = () => {
            let newUspirit = this.state.uSpirit - lossDif;
            this.setState({
                uSpirit: newUspirit
            })
        }

        // Result Decision

        if (userD1Value === userD2Value && userValue > enemyValue) {
            this.setState({
                result: "dodge attack"
            })
        } else if (enemyD1Value === enemyD2Value && userValue < enemyValue) {
            this.setState({
                result: "missed"
            })
        } else if (enemyD1Value === enemyD2Value && userD1Value === userD2Value) {
            this.setState({
                result: 'block'
            })
        }

        if (userValue > enemyValue) {
            this.setState({
                result: "winner"
            })
            winSubract()
        } else if (userValue === enemyValue) {
            this.setState({
                result: "draw"
            })
        } else {
            this.setState({
                result: "Looser"
            })
            looseSubract()
        };

        this.setState({
            userDice1: userD1Value,
            userDice2: userD2Value,
            enemyDice1: enemyD1Value,
            enemyDice2: enemyD2Value,

        })
    }











    render() {

        if (this.state.eSpirit <= 0) {
            this.resetSpirit();
            this.addUserPoint();
        } else if (this.state.uSpirit <= 0) {
            this.resetSpirit();
            this.addEnenmyPoint();
        }




        return (
            <div className="game-screen">
                <div className='container'>
                    <div className='dice-container'>

                    </div>
                    <h1> user score : {this.state.userDice1 + this.state.userDice2}</h1>
                    <h4>{this.state.userWins}</h4>
                    <h3> {this.state.userDice1} : {this.state.userDice2}</h3>
                    <h1> CPU score: {this.state.enemyDice1 + this.state.enemyDice2}</h1>
                    <h4>{this.state.enemyWins}</h4>
                    <h3> {this.state.enemyDice1} : {this.state.enemyDice2}</h3>

                    <h2>{this.state.result}</h2>
                    <h3>user Spirit: {this.state.uSpirit}</h3>
                    <h3>enemy Spirit: {this.state.eSpirit} </h3>
                    <button onClick={this.rollDice} >Roll</button>
                    <br></br>
                    <br></br>
                    <button onClick={this.resetGame}> Reset Game</button>

                </div>
            </div>
        );
    }
}


export default Game;
