import React from 'react';



class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userDice1: 0,
            userDice2: 0,
            enemyDice1: 0,
            enemyDice2: 0,
            result: " ",
            eSpirit: 30,
            uSpirit: 30,


        };
    }





    rollDice = () => {
        const userD1Value = Math.floor(Math.random() * 6 + 1);
        const userD2Value = Math.floor(Math.random() * 6 + 1);
        const enemyD1Value = Math.floor(Math.random() * 6 + 1);
        const enemyD2Value = Math.floor(Math.random() * 6 + 1);

        let userValue = userD1Value + userD2Value;
        let enemyValue = enemyD1Value + enemyD2Value;

        console.log(this.state.userDice1, 'and', this.state.userDice2)
        console.log(userD1Value, "and" ,userD2Value)
        console.log("e", this.state.enemyDice1, 'and', this.state.enemyDice2)
        console.log("e", enemyD1Value, "and" ,enemyD2Value)

        const winSubract = () => {
            const winDif = userValue - enemyValue;


            let newEspirit = this.state.eSpirit - winDif;

            this.setState({
                eSpirit: newEspirit
            })
        }

        const looseSubract = () => {
            const looseDif = enemyValue - userValue;


            let newUspirit = this.state.uSpirit - looseDif;

            this.setState({
                uSpirit: newUspirit
            })
        }



        if (userValue > enemyValue) {
           this.setState({
                result: "winner"
            })
            winSubract()
        } else if (userValue === enemyValue) {
            this.setState({
                result: "draw"
            })
        } else {
            this.setState({
                result: "Looser"
            })
            looseSubract()
        };

        this.setState({
            userDice1: userD1Value,
            userDice2: userD2Value,
            enemyDice1: enemyD1Value,
            enemyDice2: enemyD2Value,

        })





    } 

    winSubract = () => {
        let userValuex = this.state.userDice1 + this.state.userDice2;
        let enemyValuex = this.state.enemyDice1 + this.state.enemyDice2;
        const winDif = userValuex - enemyValuex;


        let newEspirit = this.state.eSpirit - winDif;

        this.setState({
            eSpirit: newEspirit
        })
    }







    render() {












        return (
            <div className="game-screen">
                <div className='container'>
                    <div className='dice-container'>

                    </div>
                    <h1> user : {this.state.userDice1 + this.state.userDice2}</h1>
                    <h1> CPU : {this.state.enemyDice1 + this.state.enemyDice2}</h1>
                    <h3>{this.state.result}</h3>
                    <h2>user Spirit: {this.state.uSpirit}</h2>
                    <h2>enemy Spirit: {this.state.eSpirit} </h2>
                    <button onClick={this.rollDice} >Roll</button>

                </div>
            </div>
        );
    }
}


export default Game;
