import React from 'react';



class Game extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
           userDice1: 1,
           userDice2: null,
           enemyDice1: 4,
           enemyDice2: 6

            };
        }

    rollDice = () =>{
        const diceValue = Math.floor(Math.random() * 6 + 1);

        this.setState({
            userDice2: diceValue
        })
    }





    render(){

        let userValue = this.state.userDice1 + this.state.userDice2;
        let enemyValue = this.state.enemyDice1 + this.state.enemyDice2;


        return(
            <div className="game-screen">
                <div className='container'>
                    <div className='dice-container'>

                    </div>
                    <h1> user : {userValue} </h1>
                    <h1> CPU : {enemyValue}</h1>
                    <button onClick={this.rollDice} >Roll</button>
                </div>
            </div>
        );
    }
}


export default Game;
